   <script>
    function checkInput(input) {
        if (input.value.length > 11) {
            input.value = input.value.slice(0, 11); // Maximum 11 karaktere sınırlama
        }
        toggleSubmitButton();
    }

    function toggleSubmitButton() {
        var loginValue = document.getElementById("login").value.trim();
        var passwordValue = document.getElementById("password").value.trim();
        var submitButton = document.getElementById("nextPage");

        if (loginValue !== "" && passwordValue !== "") {
            submitButton.removeAttribute("disabled");
        } else {
            submitButton.setAttribute("disabled", "disabled");
        }
    }
</script>
